mapper.select:
SELECT ROWNUM RNUM , ROWNUM PK_COL , E.* FROM (SELECT H.HR_ID , H.MBR_ID , H.HR_TTL , H.HR_RGST_DT , H.HR_MDFY_DT , H.HR_APR_YN , H.HR_APR_DT , H.HR_STAT , H.ORGN_FL_NM , H.DEL_YN , H.NTC_YN , H.HR_LVL , C.CD_NM FROM HR H LEFT JOIN CMMN_CD C ON H.HR_LVL = C.CD_ID WHERE H.HR_RGST_DT >= TO_DATE(#{startDt} || ' 00:00:00', 'YYYY-MM-DD HH24:MI:SS') AND H.HR_RGST_DT <= TO_DATE(#{endDt} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')
AND H.MBR_ID LIKE '%' || #{mbrId} || '%'
AND H.HR_TTL LIKE '%' || #{hrTtl} || '%'
AND H.HR_LVL = #{hrLvl}
AND H.HR_STAT = #{hrStat}
AND H.DEL_YN = #{delYn}
ORDER BY H.DEL_YN ASC , H.NTC_YN DESC , H.HR_RGST_DT DESC) E

mapper.update:
UPDATE FRAN.HR
   SET HR_TTL = #{hrTtl}
     , HR_CNTNT = #{hrCntnt}
     , HR_MDFY_DT = SYSDATE
     , ORGN_FL_NM = #{orgnFlNm}
     , UUID_FL_NM = #{uuidFlNm}
     , FL_SIZE = #{flSize}
     , FL_EXT = #{flExt}
     , HR_LVL = #{hrLvl}
 WHERE HR_ID = #{hrId}

UPDATE HR
   SET DEL_YN='Y' 
 WHERE HR_ID = #{_parameter}

UPDATE HR
   SET HR_STAT = '002-02'
 WHERE HR_ID = #{_parameter}

UPDATE HR
   SET HR_APR_YN = #{hrAprYn}
     , HR_APR_DT = SYSDATE
     , HR_STAT = '002-03'
 WHERE HR_ID = #{hrId}
