mapper.update:
UPDATE FRAN.HR
   SET HR_TTL = #{hrTtl}
     , HR_CNTNT = #{hrCntnt}
     , HR_MDFY_DT = SYSDATE
     , ORGN_FL_NM = #{orgnFlNm}
     , UUID_FL_NM = #{uuidFlNm}
     , FL_SIZE = #{flSize}
     , FL_EXT = #{flExt}
     , HR_LVL = #{hrLvl}
 WHERE HR_ID = #{hrId}

UPDATE HR
   SET DEL_YN='Y' 
 WHERE HR_ID = #{_parameter}

UPDATE HR
   SET HR_STAT = '심사중'
 WHERE HR_ID = #{_parameter}

UPDATE HR
   SET HR_APR_YN = #{hrAprYn}
     , HR_APR_DT = SYSDATE
     , HR_STAT = '심사완료'
 WHERE HR_ID = #{hrId}
