mapper.select:
SELECT MBR_LGN_FL_CNT
  FROM FRAN.MBR
 WHERE MBR_ID=#{_parameter}

SELECT COUNT(1) 
  FROM FRAN.MBR
 WHERE MBR_ID=#{_parameter}

SELECT M.MBR_ID PK_COL , DENSE_RANK() OVER(ORDER BY M.MBR_NM ASC) RNUM , M.MBR_ID , M.MBR_NM , M.MBR_EML , TO_CHAR(M.MBR_LEAV_DT, 'YYYY-MM-DD HH24:MI:SS') MBR_LEAV_DT , TO_CHAR(M.MBR_RCNT_LGN_DT, 'YYYY-MM-DD HH24:MI:SS') MBR_RCNT_LGN_DT , M.MBR_RCNT_LGN_IP , M.MBR_LGN_FL_CNT , M.MBR_LGN_BLCK_YN , TO_CHAR(M.MBR_LST_LGN_FL_DT, 'YYYY-MM-DD HH24:MI:SS') MBR_LST_LGN_FL_DT , TO_CHAR(M.MBR_PWD_CHNG_DT, 'YYYY-MM-DD HH24:MI:SS') MBR_PWD_CHNG_DT , CD.CD_NM MBR_LVL , CD.CD_ID , M.MBR_PY_MN , TO_CHAR(M.MBR_RGSTR_DT, 'YYYY-MM-DD HH24:MI:SS') MBR_RGSTR_DT , M.DEL_YN FROM MBR M INNER JOIN CMMN_CD CD ON M.MBR_LVL = CD.CD_ID WHERE M.MBR_RGSTR_DT >= TO_DATE(#{startDt} || ' 00:00:00', 'YYYY-MM-DD HH24:MI:SS') AND M.MBR_RGSTR_DT <= TO_DATE(#{endDt} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')
AND CD.CD_ID = #{mbrLvl}
AND M.MBR_NM '%' || #{mbrNm} || '%'
AND M.DEL_YN = #{delYn}
ORDER BY M.MBR_NM ASC

mapper.update:
UPDATE FRAN.MBR
   SET MBR_NM=#{mbrNm}
 WHERE MBR_ID=#{mbrId}
   AND DEL_YN='N'

UPDATE FRAN.MBR
   SET MBR_PWD=#{mbrPwd}
   	 , MBR_PWD_SLT=#{mbrPwdSlt}
 WHERE MBR_ID=#{mbrId}
   AND DEL_YN='N'

UPDATE FRAN.MBR
   SET  MBR_LVL=#{mbrLvl}
  	  , STR_ID =#{strId}	 
 WHERE MBR_ID=#{mbrId}
   AND DEL_YN='N'
