mapper.select:
SELECT MBR_LGN_FL_CNT
  FROM FRAN.MBR
 WHERE MBR_ID=#{_parameter}

SELECT COUNT(1) 
  FROM FRAN.MBR
 WHERE MBR_ID=#{_parameter}

mapper.update:
UPDATE FRAN.MBR
   SET MBR_NM=#{mbrNm}
   	 , MBR_PWD=#{mbrPwd}
   	 , MBR_PWD_SLT=#{mbrPwdSlt}
   	 , USE_YN= #{useYn}
   	 , DEL_YN='N' 
 WHERE MBR_ID=#{mbrId}
