mapper.update:
		UPDATE FRAN.MNGR_BRD
           SET MNGR_BRD_TTL = #{mngrBrdTtl}
             , MNGR_BRD_CNTNT = #{mngrBrdCntnt}      
             , MDFY_DT = SYSDATE
             , MDFYR = #{mdfyr}
             , USE_YN = NVL(#{useYn},'N')
             , NTC_YN = NVL(#{ntcYn},'N')
		 WHERE MNGR_BRD_ID = #{mngrBrdId}

	    UPDATE FRAN.MNGR_BRD
           SET DEL_YN='Y' 
		 WHERE MNGR_BRD_ID = #{_parameter}
