mapper.select:
SELECT NT_ID , SNDR_ID , RCVR_ID , NT_RD_DT , NT_TTL , DEL_YN FROM NT WHERE 1 = 1
AND NT_TTL LIKE '%' || #{ntTtl} || '%'
AND SNDR_ID = #{sndrId}
AND RCVR_ID = #{rcvrId}
ORDER BY NT_ID DESC

mapper.update:
		UPDATE NT
           SET NT_TTL = #{ntTtl}
             , NT_CNTNT = #{ntCntnt}
         WHERE NT_ID = #{ntId}

		UPDATE NT
           SET DEL_YN = 'Y' 
         WHERE NT_ID = #{ntId}
