mapper.select:
SELECT NT_ID PK_COL , DENSE_RANK() OVER(ORDER BY NT_ID DESC)RNUM , NT_ID , SNDR_ID , RCVR_ID , NT_SNDR_DT , NT_RD_DT , NT_TTL , DEL_YN FROM NT WHERE NT_SNDR_DT >= TO_DATE(#{startDt} || ' 00:00:00', 'YYYY-MM-DD HH24:MI:SS') AND NT_SNDR_DT <= TO_DATE(#{endDt} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')
AND NT_TTL LIKE '%' || #{ntTtl} || '%'
AND SNDR_ID = #{sndrId}
AND RCVR_ID = #{rcvrId}
AND USE_YN = #{useYn}
AND DEL_YN = #{delYn}
ORDER BY NT_SNDR_DT DESC

mapper.update:
		UPDATE NT
           SET NT_TTL = #{ntTtl}
             , NT_CNTNT = #{ntCntnt}
         WHERE NT_ID = #{ntId}

		UPDATE NT
           SET DEL_YN = 'Y' 
         WHERE NT_ID = #{ntId}

mapper.select:
SELECT NT_ID PK_COL , DENSE_RANK() OVER(ORDER BY NT_ID DESC)RNUM , NT_ID , SNDR_ID , RCVR_ID , NT_SNDR_DT , NT_RD_DT , NT_TTL FROM NT WHERE RCVR_ID = #{rcvrId} AND DEL_YN = 'N' AND NT_SNDR_DT >= TO_DATE(#{startDt} || ' 00:00:00', 'YYYY-MM-DD HH24:MI:SS') AND NT_SNDR_DT <= TO_DATE(#{endDt} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')
AND NT_TTL LIKE '%' || #{ntTtl} || '%'
AND SNDR_ID = #{sndrId}

SELECT COUNT(1)
  FROM MBR
 WHERE MBR_ID = #{_parameter}
