mapper.update:
UPDATE ODR_DTL
   SET ODR_DTL_PRDT_CNT = #{odrDtlPrdtCnt}
     , ODR_DTL_PRC = #{odrDtlPrc}
 WHERE ODR_DTL_ID = #{odrDtlId}

UPDATE ODR_DTL
   SET DEL_YN = 'Y'
 WHERE ODR_DTL_ID = #{odrDtlId}

UPDATE ODR_DTL
   SET DEL_YN = 'Y'
 WHERE ODR_LST_ID = #{odrLstId}

mapper.select:
SELECT * FROM (SELECT OL.ODR_LST_ID , OD.ODR_DTL_STR_ID , S.STR_NM , OD.ODR_DTL_PRDT_ID , P.PRDT_NM , OD.ODR_DTL_PRDT_CNT , OD.ODR_DTL_PRC , OD.MBR_ID , R.MBR_NM , OL.ODR_LST_ODR_PRCS , C.CD_ID , C.CD_NM , TO_CHAR(OL.ODR_LST_RGST_DT, 'YYYY-MM-DD HH24:MI:SS') ODR_LST_RGST_DT , OL.ODR_LST_RGSTR , TO_CHAR(OL.MDFY_DT, 'YYYY-MM-DD HH24:MI:SS') MDFY_DT , OL.MDFYR , M.MBR_NM MDFYR_NM FROM ODR_DTL OD LEFT OUTER JOIN ODR_LST OL ON OD.ODR_LST_ID = OL.ODR_LST_ID LEFT OUTER JOIN MBR R ON OL.MBR_ID = R.MBR_ID LEFT OUTER JOIN STR S ON OL.STR_ID = S.STR_ID LEFT OUTER JOIN MBR M ON OL.MDFYR = M.MBR_ID INNER JOIN PRDT P ON OD.ODR_DTL_PRDT_ID = P.PRDT_ID INNER JOIN CMMN_CD C ON OL.ODR_LST_ODR_PRCS = C.CD_ID WHERE OL.DEL_YN = 'N' AND OL.USE_YN = 'Y' AND OL.ODR_LST_ODR_PRCS = '003-04' ) WHERE ODR_DTL_STR_ID LIKE NVL(#{odrDtlStrId}, '%') AND ODR_DTL_PRDT_ID LIKE NVL (#{odrDtlPrdtId}, '%')
AND SUBSTR(MDFY_DT, 0, 10) = #{oneDay}
AND SUBSTR(MDFY_DT, 0, 10) >= #{startDt} AND #{endDt} >= SUBSTR(MDFY_DT, 0, 10)
