mapper.select:
SELECT COUNT(1)
  FROM ODR_LST
 WHERE MBR_ID = #{mbrId}
   AND STR_ID = #{odrDtlStrId}
   AND ODR_LST_ODR_PRCS = '003-01'

mapper.update:
UPDATE ODR_LST
   SET ODR_LST_ODR_PRCS = #{odrLstOdrPrcs}
     , MDFY_DT = SYSDATE
     , MDFYR = #{mdfyr}
 WHERE ODR_LST_ID = #{odrLstId}

UPDATE ODR_LST
   SET DEL_YN = 'Y'
     , MDFY_DT = SYSDATE
     , MDFYR = #{mdfyr}
 WHERE ODR_LST_ID = #{odrLstId}
