mapper.select:
SELECT P.PRDT_ID , P.PRDT_NM , P.PRDT_PRC , P.PRDT_CNTNT , P.PRDT_FILE_ID , P.PRDT_SRT , CMMN.CD_NM , P.PRDT_RGSTR , R.MBR_NM RGSTR_NM , P.PRDT_RGST_DT , P.MDFYR , M.MBR_NM MDFYR_NM , P.MDFY_DT , P.USE_YN , P.DEL_YN FROM PRDT P LEFT JOIN CMMN_CD CMMN ON P.PRDT_SRT = CMMN.CD_ID LEFT JOIN MBR R ON P.PRDT_RGSTR = R.MBR_ID LEFT JOIN MBR M ON P.MDFYR = M.MBR_ID WHERE P.DEL_YN = 'N'
AND P.PRDT_SRT = #{prdtSrt}
AND P.PRDT_NM LIKE '%' || #{prdtNm} || '%'
AND P.USE_YN LIKE #{useYn}
ORDER BY P.PRDT_ID DESC

mapper.update:
UPDATE PRDT 
   SET PRDT_NM = #{prdtNm} 
     , PRDT_PRC = #{prdtPrc} 
     , PRDT_CNTNT = #{prdtCntnt} 
     , PRDT_FILE_ID = #{prdtFileId} 
     , PRDT_SRT = #{prdtSrt} 
     , MDFYR = #{mdfyr} 
     , MDFY_DT = SYSDATE 
     , USE_YN = NVL(#{useYn}, 'N')  
 WHERE PRDT_ID = #{prdtId}

UPDATE PRDT 
   SET DEL_YN = 'Y' 
 WHERE PRDT_ID = #{_parameter}
