mapper.select:
SELECT P.PRDT_ID , P.PRDT_NM , P.PRDT_PRC , P.PRDT_CNTNT , P.PRDT_SRT , ORGN_FL_NM , UUID_FL_NM , FL_SIZE , FL_EXT , CMMN.CD_NM , P.PRDT_RGSTR , R.MBR_NM RGSTR_NM , P.PRDT_RGST_DT , P.MDFYR , M.MBR_NM MDFYR_NM , P.MDFY_DT , P.USE_YN , P.DEL_YN , EP.EVNT_PRDT_ID , EP.EVNT_ID , EP.EVNT_TTL , EP.EVNT_PRDT_CHNG_PRC , EP.EVNT_STRT_DT , EP.EVNT_END_DT FROM PRDT P LEFT JOIN CMMN_CD CMMN ON P.PRDT_SRT = CMMN.CD_ID LEFT JOIN MBR R ON P.PRDT_RGSTR = R.MBR_ID LEFT JOIN MBR M ON P.MDFYR = M.MBR_ID LEFT JOIN (SELECT EP.EVNT_PRDT_ID , EP.PRDT_ID , EP.EVNT_PRDT_CHNG_PRC , E.EVNT_ID , E.EVNT_TTL , E.EVNT_STRT_DT , E.EVNT_END_DT FROM EVNT_PRDT EP INNER JOIN EVNT E ON EP.EVNT_ID = E.EVNT_ID WHERE E.USE_YN = 'Y' AND EP.DEL_YN = 'N' AND SYSDATE >= E.EVNT_STRT_DT AND E.EVNT_END_DT >= SYSDATE) EP ON P.PRDT_ID = EP.PRDT_ID WHERE P.DEL_YN = 'N'
AND P.PRDT_SRT = #{prdtSrt}
AND P.PRDT_NM LIKE '%' || #{prdtNm} || '%'
AND P.USE_YN LIKE #{useYn}
ORDER BY P.PRDT_NM ASC

SELECT P.PRDT_ID , P.PRDT_NM , P.PRDT_PRC , P.PRDT_CNTNT , P.PRDT_SRT , ORGN_FL_NM , UUID_FL_NM , FL_SIZE , FL_EXT , CMMN.CD_NM , P.PRDT_RGSTR , R.MBR_NM RGSTR_NM , P.PRDT_RGST_DT , P.MDFYR , M.MBR_NM MDFYR_NM , P.MDFY_DT , P.USE_YN , P.DEL_YN , EP.EVNT_PRDT_ID , EP.EVNT_ID , EP.EVNT_TTL , EP.EVNT_PRDT_CHNG_PRC , EP.EVNT_STRT_DT , EP.EVNT_END_DT FROM PRDT P LEFT JOIN CMMN_CD CMMN ON P.PRDT_SRT = CMMN.CD_ID LEFT JOIN MBR R ON P.PRDT_RGSTR = R.MBR_ID LEFT JOIN MBR M ON P.MDFYR = M.MBR_ID LEFT JOIN (SELECT EP.EVNT_PRDT_ID , EP.PRDT_ID , EP.EVNT_PRDT_CHNG_PRC , E.EVNT_ID , E.EVNT_TTL , E.EVNT_STRT_DT , E.EVNT_END_DT FROM EVNT_PRDT EP INNER JOIN EVNT E ON EP.EVNT_ID = E.EVNT_ID WHERE E.USE_YN = 'Y' AND EP.DEL_YN = 'N' AND SYSDATE >= E.EVNT_STRT_DT AND E.EVNT_END_DT >= SYSDATE) EP ON P.PRDT_ID = EP.PRDT_ID WHERE P.DEL_YN = 'N' AND P.USE_YN = 'Y'
AND P.PRDT_SRT = #{prdtSrt}
AND P.PRDT_NM LIKE '%' || #{prdtNm} || '%'
AND P.USE_YN LIKE #{useYn}
ORDER BY EP.EVNT_ID ASC, P.PRDT_NM ASC

mapper.insert:

	SELECT 'PD-' || TO_CHAR(SYSDATE, 'YYYYMMDD') || '-' || LPAD(SEQ_PRDT_PK.NEXTVAL, 5, '0')
	  FROM DUAL

INSERT INTO PRDT
( PRDT_ID
, PRDT_NM
, PRDT_PRC
, PRDT_CNTNT
, PRDT_SRT
, ORGN_FL_NM
, UUID_FL_NM
, FL_SIZE
, FL_EXT
, PRDT_RGSTR
, PRDT_RGST_DT
, MDFYR
, MDFY_DT
, USE_YN
, DEL_YN)
VALUES
( #{prdtId}
, #{prdtNm}
, #{prdtPrc}
, #{prdtCntnt}
, #{prdtSrt}
, #{orgnFlNm}
, #{uuidFlNm}
, #{flSize}
, #{flExt}
, #{prdtRgstr}
, SYSDATE
, #{mdfyr}
, SYSDATE
, NVL(#{useYn}, 'N')
, 'N' )

mapper.update:
UPDATE PRDT 
   SET PRDT_NM = #{prdtNm} 
     , PRDT_PRC = #{prdtPrc} 
     , PRDT_CNTNT = #{prdtCntnt} 
     , PRDT_SRT = #{prdtSrt} 
     , ORGN_FL_NM = #{orgnFlNm}
	 , UUID_FL_NM = #{uuidFlNm}
	 , FL_SIZE = #{flSize}
	 , FL_EXT = #{flExt}
     , MDFYR = #{mdfyr} 
     , MDFY_DT = SYSDATE 
     , USE_YN = NVL(#{useYn}, 'N')  
 WHERE PRDT_ID = #{prdtId}

UPDATE PRDT 
   SET MDFYR = #{mdfyr} 
     , MDFY_DT = SYSDATE 
     , USE_YN = NVL(#{useYn}, 'N')  
 WHERE PRDT_ID IN

	#{id}

UPDATE PRDT 
   SET DEL_YN = 'Y' 
 WHERE PRDT_ID = #{_parameter}
