mapper.update:
UPDATE STR
   SET STR_NM=#{strNm}
	 , STR_ADDR=#{strAddr}
	 , STR_CALL_NUM=#{strCallNum}
	 , MBR_ID=#{mbrId}
	 , TO_CHAR(STR_OPN_TM, 'HH24:MI:SS') STR_OPN_TM
 	 , TO_CHAR(STR_CLS_TM, 'HH24:MI:SS') STR_CLS_TM
	 , USE_YN='N' 
 WHERE STR_ID=#{strId}

UPDATE STR
   SET STR_NM=#{strNm}
	 , STR_CALL_NUM=#{strCallNum}
	 , TO_CHAR(STR_OPN_TM, 'HH24:MI:SS') STR_OPN_TM
 	 , TO_CHAR(STR_CLS_TM, 'HH24:MI:SS') STR_CLS_TM
 WHERE STR_ID=#{strId}

UPDATE STR
   SET DEL_YN='Y' 
 WHERE STR_ID=#{_parameter}
