mapper.select:
SELECT S.STR_ID PK_COL , S.STR_ID , S.STR_NM , S.STR_LCTN , S.STR_CTY , S.STR_ADDR , S.STR_CALL_NUM , S.MBR_ID , TO_CHAR(S.STR_OPN_TM, 'HH24:MI:SS') STR_OPN_TM , TO_CHAR(S.STR_CLS_TM, 'HH24:MI:SS') STR_CLS_TM , S.STR_RGSTR , TO_CHAR(S.STR_RGST_DT, 'YYYY-MM-DD') STR_RGST_DT , S.MDFYR , TO_CHAR(S.MDFY_DT, 'YYYY-MM-DD') MDFY_DT , S.USE_YN , S.DEL_YN FROM STR S LEFT JOIN MBR M ON S.MBR_ID = M.MBR_ID WHERE S.DEL_YN = 'N'
AND S.STR_NM LIKE '%' || #{strNm} || '%'
AND S.STR_ID = #{strId}

SELECT COUNT(1)
  FROM STR
 WHERE STR_NM = #{_parameter}

SELECT COUNT(1)
  FROM STR
 WHERE STR_ADDR = #{_parameter}

SELECT COUNT(1)
  FROM STR
 WHERE STR_CALL_NUM = #{_parameter}

SELECT COUNT(1)
  FROM STR
 WHERE STR_ID= #{_parameter}

mapper.update:
UPDATE STR
   SET STR_NM=#{strNm}
	 , STR_LCTN=#{strLctn}
	 , STR_CTY=#{strCty}
	 , STR_ADDR=#{strAddr}
	 , STR_CALL_NUM=#{strCallNum}
	 , MBR_ID=#{mbrId}
	 , STR_OPN_TM =TO_DATE(#{strOpnTm}, 'HH24:MI:SS')
 	 , STR_CLS_TM =TO_DATE(#{strClsTm}, 'HH24:MI:SS')
	 , STR_RGSTR=#{strRgstr}
 	 , STR_RGST_DT=SYSDATE
	 , MDFYR=#{mdfyr}
	 , MDFY_DT=SYSDATE
	 , USE_YN= NVL(#{useYn}, 'N')
 WHERE STR_ID=#{strId}

UPDATE STR
   SET STR_NM=#{strNm}
	 , STR_CALL_NUM=#{strCallNum}
	 , TO_DATE(STR_OPN_TM, 'HH24:MI:SS') STR_OPN_TM
 	 , TO_DATE(STR_CLS_TM, 'HH24:MI:SS') STR_CLS_TM
 	 , STR_RGSTR=#{strRgstr}
 	 , STR_RGST_DT=SYSDATE
	 , MDFYR=#{mdfyr}
	 , MDFY_DT=SYSDATE
	 , USE_YN= NVL(#{useYn}, 'N')
 WHERE STR_ID=#{strId}

UPDATE STR
   SET DEL_YN = 'Y' 
 WHERE STR_ID = #{_parameter}
