mapper.insert:
INSERT INTO STR_PRDT (STR_PRDT_ID , STR_ID , PRDT_ID , MDFYR , MDFY_DT , USE_YN , DEL_YN) SELECT 'SP-' || TO_CHAR(SYSDATE, 'YYYYMMDD') || '-' || LPAD(SEQ_STR_PRDT_PK.NEXTVAL, 5, '0') , STR_ID , PRDT_ID , MDFYR , SYSDATE , 'N' , 'N' FROM (
SELECT #{strPrdt.strId} STR_ID , #{strPrdt.prdtId} PRDT_ID , #{strPrdt.mdfyr} MDFYR FROM DUAL
)

mapper.update:
UPDATE STR_PRDT 
   SET MDFYR = #{mdfyr} 
     , MDFY_DT = SYSDATE
     , USE_YN = NVL(#{useYn}, 'N') 
 WHERE STR_PRDT_ID = #{strPrdtId}

UPDATE STR_PRDT
   SET DEL_YN = 'Y'
 WHERE PRDT_ID = #{_pratemter}

UPDATE STR_PRDT
   SET DEL_YN = 'Y'
 WHERE STR_ID = #{_pratemter}
