mapper.select:
SELECT SP.STR_PRDT_ID PK_COL , DENSE_RANK() OVER(ORDER BY STR_PRDT_ID ASC) RNUM , SP.STR_PRDT_ID , SP.STR_ID , S.STR_NM , SM.MBR_ID , SM.MBR_NM , SP.PRDT_ID , P.PRDT_SRT , C.CD_NM , P.PRDT_NM , P.PRDT_PRC , SP.MDFYR , M.MBR_NM MDFYR_NM , SP.MDFY_DT , SP.USE_YN , SP.DEL_YN , EP.EVNT_PRDT_ID , EP.EVNT_ID , EP.EVNT_PRDT_CHNG_PRC , EP.EVNT_STRT_DT , EP.EVNT_END_DT FROM STR_PRDT SP INNER JOIN STR S ON SP.STR_ID = S.STR_ID INNER JOIN PRDT P ON SP.PRDT_ID = P.PRDT_ID LEFT OUTER JOIN MBR SM ON S.MBR_ID = SM.MBR_ID INNER JOIN CMMN_CD C ON P.PRDT_SRT = C.CD_ID LEFT OUTER JOIN MBR M ON SP.MDFYR = M.MBR_ID LEFT JOIN (SELECT EP.EVNT_PRDT_ID , EP.PRDT_ID , EP.EVNT_ID , EP.EVNT_PRDT_CHNG_PRC , E.EVNT_STRT_DT , E.EVNT_END_DT FROM EVNT_PRDT EP INNER JOIN EVNT E ON EP.EVNT_ID = E.EVNT_ID WHERE E.USE_YN = 'Y' AND EP.DEL_YN = 'N' AND SYSDATE >= E.EVNT_STRT_DT AND E.EVNT_END_DT >= SYSDATE) EP ON P.PRDT_ID = EP.PRDT_ID WHERE SP.DEL_YN = 'N' AND P.USE_YN = 'Y'
AND SP.STR_ID = #{strId}
AND SP.USE_YN = #{useYn}
AND C.CD_ID = #{cmmnCdVO.cdId}
AND SP.PRDT_ID = #{prdtId}
AND EP.EVNT_ID IS
NOT
NULL
ORDER BY S.STR_NM ASC

SELECT SP.STR_PRDT_ID , SP.STR_ID , S.STR_NM , SM.MBR_ID , SM.MBR_NM , SP.PRDT_ID , P.PRDT_SRT , C.CD_NM , P.PRDT_NM , P.PRDT_PRC , SP.MDFYR , M.MBR_NM MDFYR_NM , SP.MDFY_DT , SP.USE_YN , SP.DEL_YN FROM STR_PRDT SP INNER JOIN STR S ON SP.STR_ID = S.STR_ID INNER JOIN PRDT P ON SP.PRDT_ID = P.PRDT_ID LEFT OUTER JOIN MBR SM ON S.MBR_ID = SM.MBR_ID INNER JOIN CMMN_CD C ON P.PRDT_SRT = C.CD_ID LEFT OUTER JOIN MBR M ON SP.MDFYR = M.MBR_ID WHERE SP.DEL_YN = 'N' AND P.USE_YN = 'Y'
AND SP.STR_ID = #{strId}
AND SP.USE_YN = #{useYn}
AND C.CD_ID = #{cmmnCdVO.cdId}
AND SP.PRDT_ID = #{prdtId}
ORDER BY S.STR_NM ASC, P.PRDT_NM

SELECT A.* , ROWNUM RNUM FROM ( SELECT SP.STR_PRDT_ID PK_COL , SP.STR_PRDT_ID , SP.STR_ID , SP.PRDT_ID , SP.USE_YN , P.PRDT_NM , P.PRDT_PRC , P.PRDT_CNTNT , P.PRDT_SRT , P.UUID_FL_NM , EP.EVNT_ID , EP.EVNT_TTL , EP.EVNT_STRT_DT , EP.EVNT_END_DT , EP.EVNT_PRDT_ID , EP.EVNT_PRDT_CHNG_PRC FROM (SELECT STR_PRDT_ID , STR_ID , PRDT_ID , USE_YN FROM STR_PRDT WHERE STR_ID = #{strId}) SP INNER JOIN STR S ON SP.STR_ID = S.STR_ID LEFT OUTER JOIN PRDT P ON SP.PRDT_ID = P.PRDT_ID LEFT OUTER JOIN (SELECT E.EVNT_ID , E.EVNT_TTL , E.EVNT_STRT_DT , E.EVNT_END_DT , EP.EVNT_PRDT_ID , EP.PRDT_ID , EP.EVNT_PRDT_CHNG_PRC FROM (SELECT EVNT_ID , EVNT_TTL , EVNT_STRT_DT , EVNT_END_DT FROM EVNT WHERE USE_YN = 'Y' AND DEL_YN = 'N' AND SYSDATE >= EVNT_STRT_DT AND EVNT_END_DT >= SYSDATE) E INNER JOIN (SELECT EVNT_PRDT_ID , EVNT_ID , PRDT_ID , EVNT_PRDT_CHNG_PRC FROM EVNT_PRDT WHERE DEL_YN = 'N')EP ON E.EVNT_ID = EP.EVNT_ID ) EP ON SP.PRDT_ID = EP.PRDT_ID
WHERE P.PRDT_SRT = #{prdtVO.prdtSrt}
ORDER BY SP.USE_YN DESC, EVNT_ID ASC, P.PRDT_NM ASC ) A

mapper.insert:
INSERT INTO STR_PRDT (STR_PRDT_ID , STR_ID , PRDT_ID , MDFYR , MDFY_DT , USE_YN , DEL_YN) SELECT 'SP-' || TO_CHAR(SYSDATE, 'YYYYMMDD') || '-' || LPAD(SEQ_STR_PRDT_PK.NEXTVAL, 5, '0') , STR_ID , PRDT_ID , MDFYR , SYSDATE , 'N' , 'N' FROM (
SELECT #{strPrdt.strId} STR_ID , #{strPrdt.prdtId} PRDT_ID , #{strPrdt.mdfyr} MDFYR FROM DUAL
)

